import "../assets/css/card.css";

import Tag from "./Tag";

function Card({
  image,
  title,
  subtitle,
  dates,
  tags,
  children,
  liveButton,
  liveButtonText,
  sourceButton,
  sourceButtonText,
  wide,
}) {
  let classes = "card";
  if (wide) {
    classes = "card wide";
  }

  let renderedTags;

  if (tags) {
    renderedTags = tags.map((tag) => {
      return <Tag text={tag} />;
    });
  }

  const handleLiveButton = () => {
    window.open(liveButton, "_blank");
  };



  return (
    <div className={classes}>
      <img src={image} alt={title} draggable={false} />
      <div className="cardContents">
        <div className="cardTitle">{title}</div>
        <div className="cardSubtitle">
          {subtitle}
          <br />
          {dates}
        </div>
        <div className="cardBody">{children}</div>
        <div className="cardTags">{renderedTags}</div>
        <div className="cardButtonContainer">
          <div class="cardButton liveButton" onClick={handleLiveButton}>
            {liveButtonText}
          </div>
          <div class="cardButton sourceButton">{sourceButtonText}</div>
        </div>
      </div>
    </div>
  );
}

export default Card;
